                                        html! {
                                            <span class={classes!("badge", badge_class, "badge-sm", "ml-auto")}>
                                                {badge}
                                            </span>
                                        }
                                    } else {
                                        html! {}
                                    }
                                } else {
                                    html! {}
                                }
                            }

