use gloo_utils::window;
use std::rc::Rc;
use yew::prelude::*;
use yewdux::prelude::*;

#[derive(Default, Clone, PartialEq, Store)]
struct ThemeState {
    current_theme: Option<String>,
}

enum ThemeMsg {
    SetTheme(String),
}

impl Reducer<ThemeState> for ThemeMsg {
    fn apply(self, state: Rc<ThemeState>) -> Rc<ThemeState> {
        match self {
            ThemeMsg::SetTheme(theme) => ThemeState {
                current_theme: Some(theme),
            }
            .into(),
        }
    }
}

#[function_component(App)]
fn app() -> Html {
    let dispatch = Dispatch::<ThemeState>::global();

    // Runs once on mount
    use_effect_with_deps(
        {
            let dispatch = dispatch.clone();
            move |_| {
                // Simulating `themeChange(false)`, if needed you can call an external JS function
                // (Here we're just logging as a placeholder)
                log::info!("themeChange(false) called");

                if dispatch.get().current_theme.is_none() {
                    let prefers_dark = window()
                        .match_media("(prefers-color-scheme: dark)")
                        .ok()
                        .flatten()
                        .map(|m| m.matches())
                        .unwrap_or(false);

                    let new_theme = if prefers_dark { "dark" } else { "light" };
                    dispatch.apply(ThemeMsg::SetTheme(new_theme.to_string()));
                }

                || {} // Cleanup function (optional, runs on unmount)
            }
        },
        (), // Empty dependencies (runs only once like `useEffect(() => {}, [])`)
    );

    let theme = dispatch.get().current_theme.clone().unwrap_or("light".to_string());

    html! {
        <div>
            <p>{ format!("Current Theme: {}", theme) }</p>
        </div>
    }
}
```

---

### **How This Works**
1. **Equivalent to `useEffect(() => {}, [])`**
   - We use `use_effect_with_deps` with an empty dependency `()`, so it only runs **once** on mount.

2. **Checking and Setting Theme**
