### üóÇÔ∏è Parser Example (`parser.rs`)

```rust
use std::collections::{HashMap, HashSet};
use std::fs;
use std::path::Path;

pub type TranslationMap = HashMap<String, String>;
pub type Translations = HashMap<String, TranslationMap>;

pub fn load_translation_files(dir: &str) -> Translations {
    let mut translations = Translations::new();

    for entry in fs::read_dir(dir).unwrap() {
        let entry = entry.unwrap();
        let path = entry.path();
        if path.extension().and_then(|s| s.to_str()) == Some("json") {
            let lang = path.file_stem().unwrap().to_str().unwrap().to_string();
            let data: TranslationMap = serde_json::from_str(&fs::read_to_string(&path).unwrap()).unwrap();
            translations.insert(lang, data);
        }
    }

    translations
}

pub struct AnalysisResult {
    pub unused: HashMap<String, Vec<String>>,
    pub missing: HashMap<String, Vec<String>>,
    pub used_keys: HashSet<String>,
    pub cleaned: Translations,
}

pub fn compare_keys(used_keys: &HashSet<String>, translations: &Translations) -> AnalysisResult {
    let mut unused = HashMap::new();
    let mut missing = HashMap::new();
    let mut cleaned = Translations::new();

    let reference = translations.get("en").expect("Missing base 'en.json'");

    for (lang, entries) in translations {
        let mut unused_keys = vec![];
        let mut missing_keys = vec![];
        let mut cleaned_entries = HashMap::new();

        for key in used_keys {
            if let Some(value) = entries.get(key) {
                cleaned_entries.insert(key.clone(), value.clone());
            } else {
                if lang != "en" {
                    missing_keys.push(key.clone());
                }
            }
        }

        for key in entries.keys() {
            if !used_keys.contains(key) {
                unused_keys.push(key.clone());
            }
        }

        unused.insert(lang.clone(), unused_keys);
        missing.insert(lang.clone(), missing_keys);
        cleaned.insert(lang.clone(), cleaned_entries);
    }

    AnalysisResult {
        unused,
        missing,
        used_keys: used_keys.clone(),
        cleaned,
    }
}
```

---

### üìä Report Example (`report.rs`)

```rust
use crate::parser::AnalysisResult;
use std::fs;
use std::path::Path;
use std::collections::HashMap;

pub fn generate(results: &AnalysisResult, dir: &str) {
    println!("--- Translation Audit Report ---");

    for (lang, unused) in &results.unused {
        println!("{} unused keys: {}", lang, unused.len());
    }

    for (lang, missing) in &results.missing {
        println!("{} missing keys: {}", lang, missing.len());
    }

    for (lang, cleaned_map) in &results.cleaned {
        let path = Path::new(dir).join(format!("{lang}.cleaned.json"));
        fs::write(&path, serde_json::to_string_pretty(cleaned_map).unwrap()).unwrap();
        println!("Generated cleaned file: {}", path.display());
    }

    println!("Total keys used in codebase: {}", results.used_keys.len());
}
```

